import React, { useContext } from 'react';
import { CartContext } from '../context/CartContext';

const fallbackImg = 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=400&q=80';

const FoodMenu = ({ foods }) => {
  const { addToCart } = useContext(CartContext);
  return (
    <div className="food-menu">
      {foods.map(food => (
        <div key={food._id} className="food-item">
          <img src={food.image || fallbackImg} alt={food.name} style={{ width: '100%', height: 120, objectFit: 'cover', borderRadius: 6, marginBottom: 8 }} onError={e => { e.target.onerror = null; e.target.src = fallbackImg; }} />
          <h4>{food.name}</h4>
          <p>{food.description}</p>
          <p>${food.price}</p>
          <button onClick={() => addToCart(food._id)}>Add to Cart</button>
        </div>
      ))}
    </div>
  );
};

export default FoodMenu;
