import React, { useContext } from 'react';
import { CartContext } from '../context/CartContext';
import { AuthContext } from '../context/AuthContext';
import api from '../utils/api';
import CartItem from '../components/CartItem';
import { useNavigate } from 'react-router-dom';

const Cart = () => {
  const { cart, removeFromCart } = useContext(CartContext);
  const { token } = useContext(AuthContext);
  const [message, setMessage] = React.useState('');
  const [orderPlaced, setOrderPlaced] = React.useState(false);
  const navigate = useNavigate();

  const total = cart.reduce((sum, item) => sum + (item.food.price * item.quantity), 0);
  const itemCount = cart.reduce((sum, item) => sum + item.quantity, 0);

  const handlePlaceOrder = async () => {
    const res = await api.post('/orders/place', {}, token);
    if (res._id) {
      setMessage('Order placed successfully!');
      setOrderPlaced(true);
      setTimeout(() => setMessage(''), 2500);
      // Empty the cart after order placed
      setTimeout(() => window.location.reload(), 1000); // quick fix: reload to trigger cart context fetch
    } else {
      setMessage(res.message || 'Failed to place order');
    }
  };

  return (
    <div className="cart-container cart-modern">
      <h2>Your Cart</h2>
      {cart.length === 0 ? <p>Cart is empty</p> : (
        <>
          <div className="cart-list">
            {cart.map(item => (
              <CartItem key={item.food._id} item={item} onRemove={removeFromCart} />
            ))}
          </div>
          <div className="cart-summary">
            <div><b>Items:</b> {itemCount}</div>
            <div><b>Total:</b> ${total.toFixed(2)}</div>
            <button className="cart-order-btn" onClick={handlePlaceOrder}>Place Order</button>
          </div>
        </>
      )}
      {message && <div className="order-success">{message}</div>}
      {orderPlaced && (
        <button style={{ marginTop: 20, background: '#27ae60', color: '#fff', border: 'none', borderRadius: 20, padding: '0.7rem 2.2rem', fontWeight: 700, fontSize: 18 }} onClick={() => navigate('/orders')}>
          View My Order
        </button>
      )}
    </div>
  );
};

export default Cart;
