import React, { useEffect, useState } from 'react';
import api from '../utils/api';
import RestaurantList from '../components/RestaurantList';
import FoodMenu from '../components/FoodMenu';

const Home = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [foods, setFoods] = useState([]);
  const [search, setSearch] = useState('');
  const [selectedRestaurant, setSelectedRestaurant] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      const res = await api.get('/restaurants');
      setRestaurants(res);
      const foodRes = await api.get('/foods');
      setFoods(foodRes);
    };
    fetchData();
  }, []);

  const handleSearch = async (e) => {
    setSearch(e.target.value);
    const res = await api.get(`/foods/search?q=${e.target.value}`);
    setFoods(res);
  };

  const handleRestaurantFilter = async (id) => {
    setSelectedRestaurant(id);
    if (id) {
      const res = await api.get(`/foods/search?restaurant=${id}`);
      setFoods(res);
    } else {
      const res = await api.get('/foods');
      setFoods(res);
    }
  };

  return (
    <div className="home-container">
      <h2>Restaurants</h2>
      <RestaurantList restaurants={restaurants} onSelect={handleRestaurantFilter} selected={selectedRestaurant} />
      <input type="text" placeholder="Search food..." value={search} onChange={handleSearch} />
      <FoodMenu foods={foods} />
    </div>
  );
};

export default Home;
